E_D("DROP TABLE IF EXISTS `qb_shoptg_content_1`;");
E_C("CREATE TABLE `qb_shoptg_content_1` (
  `rid` mediumint(7) NOT NULL AUTO_INCREMENT,
  `id` mediumint(7) NOT NULL DEFAULT '0',
  `fid` mediumint(7) NOT NULL DEFAULT '0',
  `uid` mediumint(7) NOT NULL DEFAULT '0',
  `content` mediumtext NOT NULL,
  `market_price` varchar(10) NOT NULL DEFAULT '',
  `shoptype` varchar(10) NOT NULL DEFAULT '',
  `min_num` int(4) NOT NULL DEFAULT '0',
  `about` mediumtext NOT NULL,
  `end_time` varchar(20) NOT NULL DEFAULT '',
  `address` varchar(150) NOT NULL DEFAULT '',
  `simple_order` tinyint(1) NOT NULL DEFAULT '0',
  `awake` mediumtext NOT NULL,
  `ifquit` tinyint(1) NOT NULL DEFAULT '0',
  `limit_num` varchar(10) NOT NULL DEFAULT '',
  PRIMARY KEY (`rid`),
  KEY `fid` (`fid`),
  KEY `id` (`id`),
  KEY `uid` (`uid`),
  KEY `sortid` (`min_num`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=gbk");
E_D("replace into `qb_shoptg_content_1` values('10','10','1','1','<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	悦来老火靓汤</p>\r\n<p>\r\n	<font color=\"#666666\">大厨精心秘制，火候够，每喝的一口都是其精华所在！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"576\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/005.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	鸿运烧味拼盘</p>\r\n<p>\r\n	<font color=\"#666666\">烧味拼盘包含烧肉、叉烧、卤水猪肚、卤水掌亦等；烧肉皮脆肉香，叉烧甘香可口，卤水猪肚等更是经过大厨秘制的卤水汁腌制，软滑入味！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"200\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/006.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	上汤/芝士h美国波士顿龙虾</p>\r\n<p>\r\n	<font color=\"#666666\">美国龙虾又称为缅因龙虾，出产于美国新泽西州到加拿大纽芬兰省，年出产量约7万吨，是世界著名的美味海产。其肉质鲜美、口感爽脆有弹性，龙虾味较浓，营养丰富。上汤能带出龙虾独特的鲜美嫩滑，芝士h能跟龙虾相得益彰，完美相融。两种烹调方法都是能让人赞口不已的一绝。</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/007.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"200\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/008.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	姜葱/避风塘/咖喱h加拿大珍宝蟹</p>\r\n<p>\r\n	<font color=\"#666666\">珍宝蟹蟹味柔和，质地坚实，味质带甜，姜葱、避风塘、咖喱等惹味做法都能与珍宝蟹互相辉映，各有风味。</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"576\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/009.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	清蒸东升斑</p>\r\n<p>\r\n	<font color=\"#666666\">东升斑又名东星斑，因产自东沙群岛，以及身上布满白色的幼细花点，形似天上的星星而得名；东升斑肉较多，而且颜色雪白，用以清蒸最能吃出其鲜甜的肉质味道，除了容易消化、滋补肝肾之外还能延年益寿。</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/010.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	红啥山羊</p>\r\n<p>\r\n	<font color=\"#666666\">东山羊其美味据说是因羊食东山岭的稀有草木所致，因此肥而不腻、食无膻味，且滋补养颜防湿热；经红珊螅肉质酥烂，惹味开胃，不膻不腻！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"200\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/011.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	驰名白切鸡</p>\r\n<p>\r\n	<font color=\"#666666\">白切鸡是粤菜里最为经典的翘首之一，以其不加配料浸熟且肉不烂为佳，皮爽肉滑无渣，吃时配上即磨的姜葱，简简单单的就能让你食指大动！ </font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"576\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/012.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	雀巢美果肉丁</p>\r\n<p>\r\n	<font color=\"#666666\">用芋丝炸成雀巢，配上腰果跟肉丁，香脆可口，口感层次分明！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"200\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/013.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	竹笙扒时蔬</p>\r\n<p>\r\n	<font color=\"#666666\">竹笙是世界上著名的珍贵食用真菌，被誉为&ldquo;山珍之王&rdquo;，&ldquo;菌中皇后&rdquo;；竹笙菌体洁白细嫩，营养价值很高且鲜美，用以高汤烹调，蔬菜吸收了竹笙的鲜，味道更为甘甜，美味又有益！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/014.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	幸福炒饭</p>\r\n<p>\r\n	<font color=\"#666666\">饭粒软硬适中带甘香，金黄的色泽，真的是每尝一口都令人感到满足的幸福！</font></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/015.jpg\" width=\"430\" /></p>\r\n<br />\r\n<br />\r\n<div>\r\n	<p style=\"font-weight: bold; font-size: 16px; color: #f66666; background-color: #f7f7f7\">\r\n		悦来粤顺酒家</p>\r\n	<p>\r\n		<font color=\"#666666\">悦来粤顺酒楼以粤、川、湘菜为主，主要原料有特有农家绿色菜。为顾客提供独具特色的健康食物，近百种粤式小炒，生猛海鲜任君选择；悦来粤顺以丰富多样的美食选择，全方位的餐饮综合服务项目：如婚宴、生日宴和海鲜套餐等；其多元化的元素缔造了特有的时尚、品味餐饮文化。</font></p>\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/001.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/002.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"576\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/003.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"286\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yuelaiyueshun2/004.jpg\" width=\"430\" /></p>\r\n</div>\r\n','998','8.7','5','今日团购: 仅售998元！原价1819元的悦来粤顺酒家“喜庆年餐”1份：悦来老火靓汤+鸿运烧味拼盘+上汤/芝士h美国波士顿龙虾（1.5斤）+姜葱/避风塘/咖喱h加拿大珍宝蟹（1.5斤）+清蒸东升斑1条（1斤以上）+红啥山羊+驰名白切鸡+雀巢美果肉丁+竹笙扒时蔬+幸福炒饭+米饭10份+茶位10位+赠送会员卡1张+免费停车2小时！迎新年限量供应！美食精华大汇集！邀请好友返利10元\r\n','2015-02-31','广州市金碧花园第三期工业大道南会所三楼','0','1.快递费10元每单，2只起全国包邮<br />\r\n2.请详细填写收货人地址、姓名、联系电话，<br />\r\n3.团购成功后3个工作日之内开始按购买顺序发货<br />\r\n4.收货请注意：当场验收，如与订单内容不符或有破损，请及时联系商家并拒收<br />\r\n5.此单不适应7天无理由退款，非质量问题不予退款','0','');");
E_D("replace into `qb_shoptg_content_1` values('11','11','1','1','<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>虫草花淮杞炖鹧鸪</span></p>\r\n<p style=\"color: #666666\">\r\n	<a href=\"http://baike.baidu.com/view/27948.htm\" target=\"_blank\">《本草纲目》中有&ldquo;鹧鸪补五脏、益心力&rdquo;，民间也有&ldquo;飞禽莫如鸪&rdquo;&ldquo;一鸪顶九鸡&rdquo;之说，足见鹧鸪营养滋补；鹧鸪不仅滋补，还低脂、低热量、低胆固醇；此外，虫草花性质平和，不寒不燥，能增强和调节人体免疫功能；淮山健脾益胃、助消化，枸杞子有补肝益肾之功，几种滋补上品与鹧鸪一同炖制，简直就是养生圣品！</a></p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/003.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>黑椒香辣h鹧鸪</span></p>\r\n<p style=\"color: #666666\">\r\n	鹧鸪体型小巧，骨细肉厚，肌肉味美，还含有人体所需的多种氨基酸及锌等多种微量无素，甚至被誉为脑黄金的牛黄酸，有着非常高的食用非常价值呢！</p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/001.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>鹧鸪肾瓦撑h饭</span></p>\r\n<p style=\"color: #666666\">\r\n	h饭因爽脆的鹧鸪肾而更为可口，同时，瓦撑紧紧锁住h饭的鲜香和温热，让送进嘴里的每一口饭都变得有滋有味！</p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/002.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>鲮鱼球炒菜心</span></p>\r\n<p style=\"color: #666666\">\r\n	鲮鱼起肉切碎挞成肉球，只有这样才使鱼球爽口弹牙，一口鲜嫩清甜的菜心，一口弹牙的鲮鱼球，回味无穷！</p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/004.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>肥叉炒菜心</span></p>\r\n<p style=\"color: #666666\">\r\n	叉烧肥厚甘香，菜心青嫩可口，十分鲜甜，青菜含有大量维生素，记得多吃哟～</p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"447\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/006.jpg\" width=\"430\" /></p>\r\n<br />\r\n<p style=\"font-weight: bold; font-size: 16px; color: #f66666\">\r\n	<span>松子南瓜烙</span></p>\r\n<p style=\"color: #666666\">\r\n	金黄香脆的松子南瓜烙让人嘴馋忍不住马上动筷，香味四溢，更是让人垂涎三尺！</p>\r\n<p align=\"center\">\r\n	<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"260\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingjijiulou/005.jpg\" width=\"430\" /></p>\r\n<br />\r\n<br />\r\n<div>\r\n	<p style=\"font-weight: bold; font-size: 16px; color: #f66666; background-color: #f7f7f7\">\r\n		楹记酒楼</p>\r\n	<p style=\"color: #666666\">\r\n		楹记酒楼是一间传统粤菜酒楼，以粤式点心、广州风味小炒作为酒楼的经营风格，全天候服务于市民；酒楼设计为中等档次，设有多间贵宾房，酒楼大大小时有450个餐位。招牌菜有滋补珍味鸡、农家海鲩、炭烧琵琶鸭及多款广州风味小炒菜式。</p>\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingji/006.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingji/005.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingji/003.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<p align=\"center\">\r\n		<img onload=''if(this.width>600)makesmallpic(this,600,800);'' border=\"0\" height=\"320\" onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" src=\"http://s.lashouimg.com/tuangou/guangzhou/yingji/002.jpg\" width=\"430\" /></p>\r\n	<br />\r\n	<br />\r\n	<p style=\"font-weight: bold; font-size: 16px; color: #f66666; background-color: #f7f7f7\">\r\n		大家点评</p>\r\n	<br />\r\n	<div style=\"color: #666666\">\r\n		<ul>\r\n			<li>\r\n				琴晚妈妈生日带她来试食，挺好找的。三个人只吃了一只鸭，打包了一只。酒店很公道，没缩水和加茶钱。吃完就可以走。另外这里的点心也很实惠，每天两款特价，茶位都是两蚊/位。 <span>－－lyy813 来自大众点评网</span></li>\r\n			<li>\r\n				第一次用网上团购来的卷到这家餐厅用餐，第一感觉是这里的人服务态度好好，上来的食品也没有因为是团购，而感觉份量少，后来因为，觉得没有青菜，额外加，服务员还会细心提醒，这个需要额外收费，好贴心，感觉非常好，也看过酒店的餐牌，感觉价格比较大众化，不会太贵。特别走的时候，还笑着和我们打招呼，就算没团购卷，下次还会再来的~-~ <span>－－to630 来自大众点评网</span></li>\r\n		</ul>\r\n	</div>\r\n</div>\r\n','135','4.3','12','团购: 仅售49元！原价135元的楹记酒楼“秋冬滋补超值鹧鸪双人套餐”1份:虫草花淮杞炖鹧鸪+黑椒香辣h鹧鸪+鹧鸪肾瓦撑h饭+肥叉炒菜心/鲮鱼球炒菜心（2选1）+松子南瓜烙+茶位2位！冬季可是进补好时节，楹记酒楼秋冬又一新奉献，千万别错过这场超值的鹧鸪宴！邀请好友返利10元','2015-03-02','广州市海珠区下渡路下渡大街5号','0','1.快递费10元每单，2只起全国包邮<br />\r\n2.请详细填写收货人地址、姓名、联系电话，<br />\r\n3.团购成功后3个工作日之内开始按购买顺序发货<br />\r\n4.收货请注意：当场验收，如与订单内容不符或有破损，请及时联系商家并拒收<br />\r\n5.此单不适应7天无理由退款，非质量问题不予退款','0','');");
E_D("replace into `qb_shoptg_content_1` values('12','12','1','1','<p style=\"font-weight:bold;font-size:16px;color:#f66666\">香热奇士 （turkey ham &amp;bacon melt）</p><p style=\"color:#666666\">食材包括：培根、火腿、火鸡胸、生菜、西红柿、洋葱、青椒、黄瓜</p><p style=\"color:#666666\">万语千言道不尽香热奇士的美妙滋味。柔嫩的鸡胸肉，低脂的火腿，松脆的培根，热融融的奶酪，配以清脆的蔬菜和独特的酱料，一切都融合在松软的赛百味面包内！是不是想再来一个？</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/002.jpg\" width=\"430\" /></p><p><br /></p><p style=\"font-weight:bold;font-size:16px;color:#f66666\">百味俱乐部（subway club）</p><p style=\"color:#666666\">百味俱乐部――百味融合</p><p style=\"color:#666666\">食材包括：牛肉、火腿、火鸡胸、生菜、西红柿、洋葱、青椒、黄瓜</p><p style=\"color:#666666\">真正的百味融合，柔嫩的鸡胸肉，浓郁美味的香烤牛肉，低脂的火腿切片，现场烤制的面包，配以清脆的蔬菜和独特的酱料，独特的味觉挑逗，舍我其谁！</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/003.jpg\" width=\"430\" /></p><p><br /></p><p style=\"font-weight:bold;font-size:16px;color:#f66666\">热朱古力</p><p style=\"color:#666666\">杯子里承载香浓热朱古力的温热，轻轻品尝，口感顺滑如丝，让热巧克力为您的悠闲时光添上甜美色彩吧～</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"663\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/004.jpg\" width=\"430\" /></p><p><br /></p><p style=\"font-weight:bold;font-size:16px;color:#f66666\">“旧街场”白咖啡</p><p style=\"color:#666666\">白咖啡并不是白色的咖啡，它保留了咖啡原有的色泽和香味，却比普通咖啡更清淡柔和，甘醇芳香！在冬季的午后来一杯匀醇可口的旧街场白咖啡，好心情油然而生！</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"550\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/005.jpg\" width=\"430\" /></p><p><br /></p><p style=\"font-weight:bold;font-size:16px;color:#f66666\">玉米杯</p><p style=\"color:#666666\">粒粒金黄饱满的玉米，甜味中搀杂着奶香；品尝间，还能感受玉米的柔情蜜意在嘴里荡漾呢～</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/006.jpg\" width=\"430\" /></p><p><br /></p><p style=\"font-weight:bold;font-size:16px;color:#f66666\">曲奇</p><p style=\"color:#666666\">曲奇最吸引人的就是爽快的口感。香浓松脆的曲奇仿佛入口即化。有花生、提子、朱古力三种口味可供选择哟！</p><p><br /></p><div><p style=\"font-weight:bold;font-size:16px;color:#f66666;background-color:#f7f7f7\">赛百味</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"286\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/007.jpg\" width=\"430\" /></p><br /><p style=\"color:#666666\">赛百味1965年在美国康涅狄格州诞生，目前在全球91个国家拥有超过33000家连锁店（在中国已超过200家连锁店），是世界上最大的“潜水艇”三明治特许经营机构。连续17年在美国《企业家》杂志中被评为全球特许经营第一名，成为国际快餐业大军的一个领先者。</p><p style=\"color:#666666\">赛百味以提供新鲜且美味的三明治著称在世界各地家喻户晓，根据每位顾客的需要和不同的口味，现场制作每一款三明治、沙拉或百味卷。所有面包、甜点都是当天烤制，蔬菜和配料更是新鲜、营养、美味、健康。</p><p style=\"color:#666666\">赛百味是健康饮食的领导者。“7种SUBWAY三明治的脂肪含量低于6克！”的广告随处可见。尤其受到都市白领及追求高品质生活人士的青睐。</p><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/009.jpg\" width=\"430\" /></p><br /><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/010.jpg\" width=\"430\" /></p><br /><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/014.jpg\" width=\"430\" /></p><br /><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/015.jpg\" width=\"430\" /></p><br /><p style=\"text-align:center;\"><img onload=''if(this.width>600)makesmallpic(this,600,800);'' onload=\"if(this.width&gt;600)makesmallpic(this,600,800);\" border=\"0\" height=\"287\" src=\"http://s.lashouimg.com/tuangou/guangzhou/saibaiwei/018.jpg\" width=\"430\" /></p><br /></div>','79','4','8','团购: 仅售38元！原价79元赛百味“经典二人餐”套餐券1张：香热奇士（6英寸）+百味俱乐部（6英寸）+热朱古力+“旧街场”白咖啡+玉米杯+曲奇！（16店通兑）赛百味风暴席卷而来！这艘满载新鲜且美味的“潜水艇”三明治诚邀您“登陆”！ 邀请好友返利10元 \r\n','2015-02-24','','1','1.快递费10元每单，2只起全国包邮<br />2.请详细填写收货人地址、姓名、联系电话，<br />3.团购成功后3个工作日之内开始按购买顺序发货<br />4.收货请注意：当场验收，如与订单内容不符或有破损，请及时联系商家并拒收<br />5.此单不适应7天无理由退款，非质量问题不予退款','0','2');");

